             [C#]
                 在QueryCompleted的事件中写入如下代码：

            		private void MapControl1_QueryCompleted(object sender, QueryCompletedEventArgs e)	
            		{
                     if (e.TotalCount > 0)
                    {
            			    ResultSet rs = new ResultSet();
                         rs.Recordsets = e.Recordsets ;
                         rs.TotalCount = e.TotalCount;
            			    DataSet dataSet = rs.ToDataSet();
            			    if(dataSet != null)
            			    {
            				    dgResult.DataSource = dataSet;
            				    dgResult.DataBind();
            			    }
                     }
            		 }

example.code:

在事件中写入如下代码：

private void MapControl1_PathFound(object sender, PathFoundEventArgs e)
{
	if(e.TotalLength > 0)
	{
		string strResult = string.Concat(e.TotalLength.ToString()," ",MapControl1.CoordUnits.ToString());
		this.DisplayResult(strResult);
	}
	else
	{
		this.DisplayResult(null);
	}
}
private void DisplayResult(string result)
{
	string scriptText = string.Empty;
	string displayResult = string.Empty;
	if(result == null || result.Equals(string.Empty))
	{	
		displayResult = "no match found!";
	}
	else
	{
		displayResult = "结果为： " + result.Replace("\"","\\\"");
	}
	Page.RegisterHiddenField("displayResult", displayResult);
	scriptText = "<script type='text/javascript'>" + "window.onload=function(){ if(document.getElementById('displayResult').value != \"\") alert(document.getElementById('displayResult').value)} </script>";
	Page.RegisterStartupScript("DisplayResult", scriptText); 
}

