//直接返回等值面实体
param.SaveResultToDataset = false;        
param.SmoothMethod = SmoothMethod.BSpline;
param.Smoothness = 3;
//根据DEM提取等值面
IsoRegionResult actualRegion = MapControl1.GridAnalystOperator.IsoRegion(param);

//离散点数据集标识
string pointDataset = "PointDT@interpolate";
//离散点数据集中的值字段名称
string pointValueField = "E_Temp";
//离散点数据集进行插值操作参数
InterpolateParam interpolateParam = new InterpolateParam();
interpolateParam.InterpolateMode = InterpolateOperateMode.IDW;
//提取等值面操作参数
IsoRegionParam param = new IsoRegionParam();
param.BaseValue = 0;
param.OverwriteIfExists = true;
param.SaveResultToDataset = true;
param.ResultDataset = "result@interpolate";
param.UseIntervals = true;
param.Intervals = new double[] { 400, 200, 300, 1500, 2000 };
param.SmoothMethod = SmoothMethod.BSpline;
param.Smoothness = 3;
//根据离散点数据集进行提取等值面操作
IsoRegionResult actualResult = MapControl1.GridAnalystOperator.IsoRegion(pointDataset, pointValueField, interpolateParam, param);

GridExecuteParam excuteParam = new GridExecuteParam();
excuteParam.MathExpression = "[gridcompute.cda51_2]+[gridcompute.gda51_2]"; // cda51_2 和 gda51_2 是gridcompute数据源中的DEM数据集
excuteParam.ResultDataset = "result@gridcompute";
excuteParam.OverwriteIfExists = true;

GridExecuteResult result = MapControl1.GridAnalystOperator.GridExecute(excuteParam);
if (!result.Succeed)
{
    ClientScript.RegisterStartupScript(typeof(Page), "error", "计算失败！"+result.Message );
}
ClientScript.RegisterStartupScript(typeof(Page), "error", result.ResultDataset);
