[C#]

 protected void Button1_Click(object sender, EventArgs e)
 {
        string netLayerName = "";
        int layerCountry = MapControl1.Layers.GetUpperBound(0) - MapControl1.Layers.GetLowerBound(0);
        for (int i = 0; i <= layerCountry; i++)
        {
            if (MapControl1.Layers[i].Type == LayerType.Network)
            {
                 netLayerName = MapControl1.Layers[i].Name;  // 获取网络图层的名称
                 break;
            }
        }
        if (netLayerName == "")  // 检查该地图是否有网络图层
        {
            return;
        }
        int nFromNode = 164;  // 获取起始点SMID
        int nToNode = 120;   // 获取终点SMID
        int[] Nodes = new int[2];
        Nodes[0] = nFromNode;
        Nodes[1] = nToNode;
        // 设置路径分析参数及高亮风格
        RouteParam rp = new RouteParam();
        rp.NetworkParams.NetworkSetting.NetworkLayerName = netLayerName;
        rp.Highlight.HighlightResult = true;
        SuperMap.IS.Utility.Style style = new SuperMap.IS.Utility.Style();
        tyle.PenColor = 255;
        style.PenWidth = 2;
        rp.Highlight.LineStyle = style;
        RouteResult routeRS = MapControl1.SpatialAnalystOperator.FindPath(Nodes, rp);  // 执行路径分析
        if (routeRS != null)
        {
            this.Label1.Text = routeRS.TotalLength.ToString();  // 获取最佳路径的总长度
        }
  }

[VB.NET]

    Protected Sub Button1_Click(ByVal sender As Object, ByVal e As System.EventArgs) Handles Button1.Click
        Dim netLayerName As String = ""
        Dim layerCountry As Integer
        layerCountry = MapControl1.Layers.GetUpperBound(0) - MapControl1.Layers.GetLowerBound(0)
        Dim i As Integer
        For i = 0 To layerCountry
            If MapControl1.Layers(i).Type = LayerType.Network Then
                 netLayerName = MapControl1.Layers(i).Name  ' 获取网络图层名
            End If
        Next
        If netLayerName = "" Then   '  检查该地图是否有网络图层
            Exit Sub
        End If
        Dim nFromNode As Integer
        Dim nToNode As Integer
        nFromNode = 125  ' 获取起点SMID
        nToNode = 256  ' 获取终点smid
        If nFromNode >= 0 And nToNode >= 0 And nFromNode <> nToNode Then
            Dim Nodes(1) As Integer
            Nodes(0) = nFromNode
            Nodes(1) = nToNode
            ' 设置路径分析参数及高亮风格
            Dim rp As New RouteParam
            rp.NetworkParams.NetworkSetting.NetworkLayerName = netLayerName
            rp.Highlight.HighlightResult = True
            Dim style As New SuperMap.IS.Utility.Style
            style.PenColor = 255
            style.PenWidth = 2
            rp.Highlight.LineStyle = style
            Dim routeRS As New RouteResult
            routeRS = MapControl1.SpatialAnalystOperator.FindPath(Nodes, rp)      '执行路径分析
            If Not routeRS Is Nothing Then
                Label1.Text = routeRS.TotalLength.ToString()   ' 获取最佳路径的总长度
            End If
        End If
     End Sub

example.code:

下例通过自定义一个几何对象，并将其赋予缓冲区查询分析的参数对象中，使其能够对该对象创建缓冲区，从而执行缓冲区查询分析的操作。
注：如果需要使用下面的示例代码，请先添加对 Utility 命名空间的引用：

    using SuperMap.IS.Utility;

[C#]

Geometry[] geometry = new Geometry[1];
geometry[0] = new Geometry();
geometry[0].Feature = FeatureType.Polygon;// 设置绘制的几何对象的类型为多边形
geometry[0].Parts = new int[] { 5 };// 设置几何对象为一个简单的对象并且通过5个简单点来确定形状
geometry[0].Points = new MapCoord[5];
geometry[0].Points[0] = new MapCoord(3900, -4000);
geometry[0].Points[1] = new MapCoord(3900, -4100);
geometry[0].Points[2] = new MapCoord(4000, -4100);
geometry[0].Points[3] = new MapCoord(4000, -4000);
geometry[0].Points[4] = new MapCoord(3900, -4000);//首尾相接

BufferQueryParam bufferQueryParam = new BufferQueryParam();
bufferQueryParam.BufferDistance = 100;//设置缓冲半径
bufferQueryParam.BufferSmoothness = 12;//设置缓冲平滑度
bufferQueryParam.BufferSourceHighlight = new Highlight();
bufferQueryParam.BufferSourceHighlight.HighlightResult = true;//设置缓冲源对象在地图上高亮显示
bufferQueryParam.QueryMode = SpatialQueryMode.AreaIntersect;//设置空间关系模式——部分包含或者全部包含
bufferQueryParam.ReturnBufferResult = true;

QueryParam queryParam = new QueryParam();
queryParam.Layers = new QueryLayer[1];
queryParam.Layers[0] = new QueryLayer();
queryParam.Layers[0].Name = "ResidentialArea@changchun";//设置查询图层（对居民区图层进行查询）
queryParam.Highlight = new Highlight();
queryParam.Highlight.HighlightQueryArea = true;//高亮查询区域
queryParam.Highlight.HighlightResult = true;//高亮查询结果对象

bufferQueryParam.QueryParam = queryParam;

BufferQueryResult result = MapControl1.SpatialAnalystOperator.BufferQuery(geometry, bufferQueryParam);//执行缓冲区查询分析，获取结果


假设当前地图有两个图层，一个是表示医院的图层 Hospital@changchun（点对象）；另一个是表示居民区的图层 ResidentialArea@changchun（面对象）。
现在卫生部需要测算处于 SmID=24 的医院的一级覆盖范围（500米）内包含哪些居民区，这个需求可以利用缓冲区查询分析来实现。
即对 Hospital@changchun 图层的这个 SmID=24 的医院点制作缓冲半径为 500 的缓冲区域，查找与该缓冲区域具有全部包含或者部分包含空间关系的居民区（图层 ResidentialArea@changchun）的几何对象。

[C#]

注：如果需要使用下面的示例代码，请先添加对 Utility 命名空间的引用：

	using SuperMap.IS.Utility;

缓冲区查询分析示例代码如下：

QueryLayer queryLayer = new QueryLayer();
queryLayer.Name = "Hospital@changchun";//设置制作缓冲区的几何对象对应的图层
queryLayer.WhereClause = "SmID=24";//设置对SMID为24的几何对象制作缓冲区

BufferQueryParam bufferQueryParam = new BufferQueryParam();
bufferQueryParam.BufferDistance = 500;//设置缓冲半径
bufferQueryParam.BufferSmoothness = 12;//设置缓冲平滑度
bufferQueryParam.BufferSourceHighlight = new Highlight();
bufferQueryParam.BufferSourceHighlight.HighlightResult = true;//设置对查询的结果在地图上高亮显示
bufferQueryParam.QueryMode = SpatialQueryMode.AreaIntersect;//设置空间关系模式——部分包含或者全部包含
bufferQueryParam.ReturnBufferResult = true;

QueryParam queryParam = new QueryParam();
queryParam.Layers = new QueryLayer[1];
queryParam.Layers[0] = new QueryLayer();
queryParam.Layers[0].Name = "ResidentialArea@changchun";//设置查询图层（对居民区图层进行查询）
