para.code:

 protected void btnFindBus_Click(object sender, EventArgs e)
 {
    string strBusStart = this.txtBusStart.Text; // 起始站点名称
    string strBusEnd = this.txtBusEnd.Text;  //终点站点名称
    int transferTimes;
    int.TryParse(this.lstChangeTimes.SelectedValue, out transferTimes); // 换乘次数
    bool bMost = this.checkBoxMostChange.Checked ? true : false; // 是否支持最多换乘方案
    BusSolutionItem solution = this.MapControl1.SpatialAnalystOperator.GetBusSolution(strBusStart, strBusEnd, transferTimes, bMost, 10); // 进行公交换乘方案的分析
    // 取得结果并显示在客户端
    if (solution != null && solution.RoutingItems != null && solution.RoutingItems.Length > 0) // 根据RoutingItems判断是否有符合条件的换乘方案
    {
        // 表明分析结果中有符合条件的换乘方案，得到最终方案，直接显示。
        System.Text.StringBuilder sb = new System.Text.StringBuilder();
        int routingCount = solution.RoutingItems.Length;
        BusRoutingItem curItem;
        sb.Append("<br>共搜索到 " + routingCount + " 种换乘方案：");
        for (int i = 0; i < routingCount; i++) // 将每一个解决方案显示在页面中
        {
            sb.Append("<br> " + "<span class=busResult> 方案"+ (i + 1) + "</span>" + ": ");
            curItem = solution.RoutingItems[i];
            string upStop;
            string busline;
            string downStop;
            for (int j = 0; j < curItem.UpStops.Length; j++)
            {
                upStop = curItem.UpStops[j].Name; // 获取上车站站名（包括中转站）
                busline = curItem.BusLines[j].Name;   // 获取每一次换乘的公车线路名称
                downStop = curItem.DownStops[j].Name;  // 获取下车站站名（包括中转站）
                sb.Append("从 " + upStop + " 站上车，乘坐 " + busline + " 汽车，在 " + downStop + " 站下车； <br>");
             }
         }
         this.lblBusResult.Text += sb.ToString(); // 显示在客户端的Label中
         return;
    }
    else  // 没有得到最终方案，可能：起点站、终点站不唯一，需要二次查询；没有匹配的方案。
    {
        if (solution != null && solution.StartStops != null && solution.EndStops != null)  // 起点站、终点站不唯一，需要二次查询。
        {
            BusStopItem[] busStartStops = solution.StartStops; // 获取包含输入的起始站站名的所有公车站点对象
            BusStopItem[] busEndStops = solution.EndStops; // 获取包含输入的终点站站名的所有公车站点对象
            this.lstBusStarts.Items.Clear();
            for (int i = 0; i < busStartStops.Length; i++)  // 将得到的所有包含起始站点名的公车站点对象列于DorpDownlist中，供用户选择唯一的起始站点
            {
                ListItem curItem = new ListItem();
                curItem.Text = busStartStops[i].Name + "(ID=" + busStartStops[i].ID + ")";
                curItem.Value = busStartStops[i].InterChangeID.ToString(System.Globalization.NumberFormatInfo.InvariantInfo);
                this.lstBusStarts.Items.Add(curItem);
            }
            this.lstBusEnds.Items.Clear();
            for (int i = 0; i <busEndStops.Length; i++)  // 将得到的所有包含终点站点名的公车站点对象列于DorpDownlist中，供用户选择唯一的终点站点
            {
                ListItem curItem = new ListItem();
                curItem.Text = busEndStops[i].Name + "(ID=" + busEndStops[i].ID + ")"; ;
                curItem.Value = busEndStops[i].InterChangeID.ToString(System.Globalization.NumberFormatInfo.InvariantInfo);
                this.lstBusEnds.Items.Add(curItem);
            }
     }
     else // 没有匹配的方案。
     {
        this.lblBusResult.Text = string.Empty;
        // 弹出提示对话框。
        string key = "ShowBusSolutionResultNothing";
        string msg = "没有查询结果！";
        // 由于SmartNav的影响, 这里借助了客户端的一个隐藏字段, 否则会每次都弹出结果对话框.
        Page.RegisterHiddenField(key, msg);
        string script;
        script = "<script language='javascript'>";
        script += "window.onload=function(){  var msg = document.getElementById('" + key + "').value; if(msg != \"\") alert(msg)} </script>";
        Page.RegisterStartupScript(key, script);
     }
}

