             [C#]

             		private void Button1_Click(object sender, System.EventArgs e)
             		{
             			int j = 0;
             			Layer[] layers = this.MapControl1.Layers;
             			int length = layers.Length;
             			for(int i = 0; i <= length; i++)
             			{
             				if(layers[i].Name == "world@world")
